  let context=useContext(notecontext)
  let {DeleteNote}=context
  const{note,updateNote}=props
  const Delete=()=>{
  DeleteNote(note._id)
  toast.dismiss("")
props.showAlert(" Note has been deleted Successfully","warning")
  }


