import React, { useContext, useState } from 'react';
import notecontext from "../Context/Notes/NoteContext";

export default function AddNote(props) {
  let context = useContext(notecontext);
  let { addNote } = context;

  const [note, setNote] = useState({ title: "", Description: "", tag: "" });

  const AddNoteInDB = (value) => {
    value.preventDefault();
    // Add note to the DB
    addNote(note.title, note.Description, note.tag);
    setNote({ title: "", Description: "", tag: "" });
    props.showAlert("Note has been Added Successfully", "secondary");
  };

  const onChange = (e) => {
    setNote({ ...note, [e.target.name]: e.target.value });
  };

  return (
    <div className="container my-5">
      <div className="row justify-content-center">
        <div className="col-md-6 col-sm-10 col-lg-4">
          <form className="p-4 shadow-lg rounded" style={{ backgroundColor: '#f8f9fa' }}>
            <h3 className="text-center mb-4">Add a New Note</h3>
            <div className="mb-3">
              <label htmlFor="title" className="form-label">Title</label>
              <input
                type="text"
                value={note.title}
                className="form-control"
                name="title"
                id="title"
                onChange={onChange}
                required
                minLength={5}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="Description" className="form-label">Description</label>
              <input
                type="text"
                value={note.Description}
                className="form-control"
                id="Description"
                name="Description"
                onChange={onChange}
                required
                minLength={5}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="tag" className="form-label">Tag</label>
              <input
                type="text"
                value={note.tag}
                className="form-control"
                id="tag"
                name="tag"
                onChange={onChange}
                required
                minLength={3}
              />
            </div>
            <button
              type="submit"
              disabled={note.title.length < 5 || note.Description.length < 5 || note.tag.length < 3}
              className="btn btn-primary w-100"
              onClick={AddNoteInDB}
            >
              Add Note
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
